package com.groovyimport java.awt.TexturePaintContext.Int;import com.sun.xml.internal.ws.api.streaming.XMLStreamReaderFactory.Default;def struct_test(){	// no do while loop	// while loop	def list = [1,2,3]	while (list) {	list.remove(0)	}	assert list == []	while (list.size() < 3) list << list.size()+1	assert list == [1,2,3]		// for loop	def store = ''	for (String s in 'a'..'c') store += s	assert store == 'abc'		def myString = 'Old school Java'	store = ''	for (i=0; i < myString.size(); i++) {	store += myString[i]	}	assert store == myString		myString = 'Java range index'	store = ''	for (i in 0 ..< myString.size()) {	store += myString[i]	}	assert store == myString}def io_test(){		// read	def baseDir = 'C:\\Users\\xiayiguo\\Desktop';	new File(baseDir, 'ip.txt').eachLine ("gbk") { line ->		println line	}		// write	new File(baseDir,'haiku.txt').withWriter('utf-8') { writer ->		writer.writeLine 'Into the ancient pond'		writer.writeLine 'A frog jumps'		writer.writeLine 'Waterâ€™s sound!'	}		new File(baseDir).eachFile { file ->		println file.name	}		def process = "cmd /c dir".execute()	println "${process.text}"}io_test();struct_test();